import React from 'react';
import styled, {css} from 'styled-components';


const Button = styled.button`
  display: block;
  border-radius: 8px;
  background: white;
  border: 5px solid black;
  font-size: 20px;
  color: black;
  ${props => props.primary && css`
    background: limegreen;
    border-color: limegreen
    color: darkgreen;
  `}
`;

export default Button;
